INSERT INTO `service_call_log` VALUES ('64', '8', null, '19012915500622329491', 'drivingQuestionsQuery', '{\"page_number\":\"2\",\"question_subject\":\"1\",\"question_type\":\"C1\"}', '2019-01-29 15:50:06', '{\"status\":\"0\",\"msg\":\"ok\",\"list\":[{\"question\":\"对未取得驾驶证驾驶机动车的，追究其法律责任。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"对\",\"explain\":\"《道路交通安全法》第九十九条规定：未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"错\",\"explain\":\"《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"...公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车在道路上违反交通安全法规的行为属于违法行为。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"对\",\"explain\":\"“违反道路交通安全法”，违反法律法规即视为违法行为。官方已无违章和违规的说法了。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车应当随身携带哪种证件？\",\"option1\":\"A、工作证\",\"option2\":\"B、驾驶证\",\"option3\":\"C、身份证\",\"option4\":\"D、职业资格证\",\"answer\":\"B\",\"explain\":\"《道路交通安全法》第十九条：驾驶人应当按照驾驶证载明的准驾车型驾驶机动车；驾驶机动车时，应当随身携带机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"}]}', '2019-01-29 15:50:06', '2019-01-29 15:50:06', '0');
