INSERT INTO `service_call_log` VALUES ('341', null, null, '19051618102742487127', 'drivingQuestionsQuery', '{\"page_number\":\"1\",\"question_subject\":\"1\",\"question_type\":\"A3\"}', '2019-05-16 18:10:27', '{\"status\":\"0\",\"msg\":\"ok\",\"list\":[{\"question\":\"驾驶机动车在道路上违反道路交通安全法的行为，属于什么行为？\",\"option1\":\"A、违章行为\",\"option2\":\"B、违法行为\",\"option3\":\"C、过失行为\",\"option4\":\"D、违规行为\",\"answer\":\"B\",\"explain\":\"违反《道路交通安全法》，属于违法行为。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？\",\"option1\":\"A、刑事责任\",\"option2\":\"B、民事责任\",\"option3\":\"C、经济责任\",\"option4\":\"D、直接责任\",\"answer\":\"A\",\"explain\":\"根据《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"quest...而不论其造成交通事故后果的大小。也就是说，法律规定惩罚的交通肇事逃逸行为，与驾驶人造成交通事故后果的轻重无关；构成犯罪的，依法追究刑事责任。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车违反道路交通安全法律法规发生交通事故属于交通违章行为。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"错\",\"explain\":\"“违反道路交通安全法”，违反法律法规即为违法行为。官方已无违章和违规的说法了。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"对\",\"explain\":\"违反道路通行规定势必要受到相应处罚的，避免下次再犯。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"}]}', '2019-05-16 18:10:33', '2019-05-16 18:10:27', '0');
INSERT INTO `service_call_log` VALUES ('342', null, null, '19051618115114556000', 'drivingQuestionsQuery', '{\"page_number\":\"2\",\"question_subject\":\"1\",\"question_type\":\"A3\"}', '2019-05-16 18:11:51', '{\"status\":\"0\",\"msg\":\"ok\",\"list\":[{\"question\":\"对未取得驾驶证驾驶机动车的，追究其法律责任。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"对\",\"explain\":\"《道路交通安全法》第九十九条规定：未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"错\",\"explain\":\"《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"...公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车在道路上违反交通安全法规的行为属于违法行为。\",\"option1\":\"\",\"option2\":\"\",\"option3\":\"\",\"option4\":\"\",\"answer\":\"对\",\"explain\":\"“违反道路交通安全法”，违反法律法规即视为违法行为。官方已无违章和违规的说法了。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"},{\"question\":\"驾驶机动车应当随身携带哪种证件？\",\"option1\":\"A、工作证\",\"option2\":\"B、驾驶证\",\"option3\":\"C、身份证\",\"option4\":\"D、职业资格证\",\"answer\":\"B\",\"explain\":\"《道路交通安全法》第十九条：驾驶人应当按照驾驶证载明的准驾车型驾驶机动车；驾驶机动车时，应当随身携带机动车驾驶证。\",\"pic\":\"\",\"type\":\"\",\"chapter\":\"道路交通安全法律、法规和规章\"}]}', '2019-05-16 18:11:57', '2019-05-16 18:11:51', '0');
